import React from 'react'
import './Game.css'
import { Cell } from '../Cell/Cell';

function range(n){
  return Array.from({length: n}, (_, i) => i);
}

export class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      grid: this.makeGrid(this.props.rows, this.props.cols)
    }
  }

  makeGrid(rows, cols){
    const grid = range(rows).map( x =>
      range(cols).map( y =>
      <Cell mine={{isMasked: true}} key={`${x} - ${y}`}/>),
      );
    return grid;
  }

  handleonUnmask(){
    this.setState({
      grid: this.state.grid.map(row => row.map((mine)=>{
        return {isMasked:false}
      })
    ),
    });
  }

  render() {
  console.log(this.props);

    return (
    <div className="game">
      <div className="grid" style={{
        gridTemplateColumns: `repeat(${this.props.cols}, 4rem)`,
        gridTemplateRows: `repeat(${this.props.rows}, 4rem)`
      }}>
        {this.state.grid.map((row,x) =>{
          return row.map((mine,y)=>{
            return <Cell mine={mine} onUnmask={this.handleonUnmask} key={`${x}-${y}`}/>
          })
        })}
      </div>
    </div>
    );
  }


}
