  constructor(){
    super();
    console.log('Game builded');
  }

  render() {
  console.log(this.props);
    const grid = range(this.props.rows).map( x =>
    range(this.props.cols).map( y =>
    <Cell mine={{isMasked: true}}/>),
    );
    console.log(grid);
    return (
    <div className="game">
      <div className="grid" style={{
        gridTemplateColumns: `repeat(${this.props.cols}, 4rem)`,
        gridTemplateRows: `repeat(${this.props.rows}, 4rem)`
      }}>
        {grid}
      </div>
    </div>
    );
  }


}
